import React,{useState} from "react";
import './ExpenseForm.css'

const ExpenseForm = () =>{
   


    const [enteredTitle,setEnteredTitle] = useState('');
    const [enteredAmount,setEnteredAmount] = useState('');
    const [enteredDate,setEnteredDate] = useState('');

    // const titleHandler = (event) =>{
    //     console.log(event.target.value);
    //     setEnteredTitle(event.target.value)
    // }
    // const amountHandler = (event) =>{
    //     console.log(event.target.value);
    //     setEnteredAmount(event.target.value);
    // }
    // const dateHandler = (event) =>{
    //     console.log(event.target.value);
    //     setEnteredDate(event.target.value);
    // }

     // one way of writng the useState hook to call each state change individually and the other is to call all the state changes together as shown below
    const [userInput,setUserInput] = useState({
        enteredTitle:'',
        enteredAmount: '',
        enteredDate:'',
    });
    // another way to call the event listener function is the following for every function
       const titleHandler = (event) =>{
        console.log(event.target.value);
        setEnteredTitle(event.target.value)
    }
    const amountHandler = (event) =>{
        console.log(event.target.value);
        setEnteredAmount(event.target.value);
    }
    const dateHandler = (event) =>{
        console.log(event.target.value);
        setEnteredDate(event.target.value);
    }
    return (<form>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label >Title</label>
                <input type="text"  />
            </div>
            <div className="new-expense__control">
                <label >Amount</label>
                <input type="number" min='0.01' step='0.01'  />
            </div>
            <div className="new-expense__control">
                <label >Date</label>
                <input type="date" min='01-01-2020' max= '31-12-2023' />
            </div>
        </div>
        <div className="new-expense__actions">
            <button type="submit">Add Expense</button>
        </div>
    </form>)

 };


    export default ExpenseForm;
